import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddNewCardPopup from './AddNewCardPopup';

import api from '../utils/Api';

function App() {

  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);


  const handleEditAvatarClick = () => {
      setEditAvatarPopupOpen(true);
  };

  const handleEditProfileClick = () => {
      setEditProfilePopupOpen(true);
  };

  const handleAddPlaceClick = () => {
      setAddPlacePopupOpen(true);
  };

  const closeAllPopups = () => {
      setEditAvatarPopupOpen(false);
      setEditProfilePopupOpen(false);
      setAddPlacePopupOpen(false);
  };


  const getUserAndCards = async () => {
        try {
            const [userInfo, cards] = await Promise.all([api.getUserInfo(), api.getCards()]);
            setCards(cards);
            setCurrentUser(userInfo);
        } catch (err) {
            console.log(`${err}`);
        }
    };

    React.useEffect(() => {
        getUserAndCards();
    }, [])



  return (
    <div className="page">
      <Header/>
      <Main onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick}/>
      <Footer/>
      <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups}/>
      <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups}/>
      <AddNewCardPopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups}/>

    </div>
  );
}

export default App;
