import React from 'react';

function Login({ handleLogin }) {
    const [data, setData] = useState({
        username: '',
        password: '',
        message: ''
      });

    const handleChange = (e) => {
        const {name, value} = e.target;

        setData({
          ...data,
          [name]: value
        })
      }

    const handleSubmit = (e) => {
        e.preventDefault();

        const { username, password } = data;
        handleLogin(username, password);
      }


    return(
        <section className="login">
            <h2 className="login__title">Вход</h2>
            <form onSubmit={handleSubmit} className="login__form">
                <input className="login__input" id="username" required name="username" type="text" placeholder="Email"/>
                <input className="login__input" id="password" required name="password" type="password" placeholder="Пароль"/>
                <button type="submit" className="button login__button">Войти</button>
            </form>
        </section>
    );
}

export default Login;
