import React from 'react';

const PopupWithForm = ({name, isOpen, onClose, children}) => {


    return (
        <article className={`${isOpen ? `popup popup_type_${name} popup_type_opened` : `popup popup_type_${name}`}`}
                 onMouseUp={handleOverlayClose}>
            <div className={`${(name === "popup__zoomed") ? `popup__zoomed` : `popup__container`}`}>
                <button className="popup__button popup__button_type_close"
                        type="button"
                        onClick={onClose}/>
                {children}
            </div>
        </article>
    );
}

export default PopupWithForm;
