import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Register({ handleRegister }) {
    const [data, setData] = useState({
        username: '',
        password: ''
      });


  const handleChange = (e) => {
    const {name, value} = e.target;

    setData({
      ...data,
      [name]: value
    })

  }

  const handleSubmit = (e) => {
    e.preventDefault();
    handleRegister({username, password });
  }

    return(
        <section className="login">
            <h2 className="login__title">Регистрация</h2>
            <form onSubmit={handleSubmit} className="login__form">
                <input className="login__input" id="username" required name="username" type="text" value={data.username} onChange={handleChange} placeholder="Email"/>
                <input className="login__input" id="password" required name="password" type="password" value={data.password} onChange={handleChange} placeholder="Пароль"/>
                <button type="submit" className="button login__button">Зарегистрироваться</button>
            </form>
            <div className="login__signin">
                <p className="login__signin-text">Уже зарегистрированы?</p>
                <Link to="/sign-in" className="login__signin-text login__signin-link">Войти</Link>
            </div>
        </section>
    );
}

export default Register;
