import React from "react";
import Popup from "./Popup";
import useValidation from "../hooks/useValidation";

const EditProfilePopup = ({isOpen, onClose, onUpdateUser, isLoading}) => {
    const fields = ['name', 'about'];

    const {
        isValid, setIsValid,
        inputValue, setInputValue,
        validationMessage, setValidationMessage,
        handleInputChange, fieldsEnumeration
    } = useValidation(fields);

    const currentUser = React.useContext(CurrentUserContext);

    const handleSubmit = (event) => {
        event.preventDefault();
        onUpdateUser({
            name: inputValue.name,
            about: inputValue.about,
        });
    };

    React.useEffect(() => {
        setInputValue({
            name: currentUser.name || '',
            about: currentUser.about || '',
        });
        setIsValid(fieldsEnumeration(true));
        setValidationMessage(fieldsEnumeration(''));
    }, [currentUser, isOpen, setInputValue, setIsValid, setValidationMessage]);

    return (
        <Popup name="edit"
               isOpen={isOpen}
               onClose={onClose}>
            <h3 className="popup__title">Редактировать профиль</h3>
            <form className={'popup__form popup__form_type_edit'} name="edit" onSubmit={handleSubmit} novalidate>
              <input type="text" id ="profilename" name="profilename" value={inputValue.name} className="popup__input popup__input_type_name" placeholder="Имя" minlength="2" maxlength="40" required/>
              <span className="popup__span-error" id="profilename-error">{validationMessage.name}</span>
              <input type="text" id="profileabout" name="profileabout" value={inputValue.about} className="popup__input popup__input_type_about" placeholder="Профессия" minlength="2" maxlength="200" required/>
              <span className="popup__span-error" id="profileabout-error">{validationMessage.about}</span>
            <button type="submit" className="popup__button popup__button_type_save popup__button_type_save-edit">Сохранить</button>
           </form>
        </Popup>
    )
}

export default EditProfilePopup;
