import React from 'react';
import Card from './Card'
import buttonAdd from '../images/add_button_plus.svg';
import buttonEdit from '../images/edit_button.svg';
import avatar from '../images/profile__avatar.jpg';
import avatarEdit from '../images/avatar_edit_pen.svg';

import api from '../utils/Api'

const Main = ({onEditAvatar, onEditProfile, onAddPlace, userName, userDescription, userAvatar, cards}) => {



    return (
        <main>
        <section className="profile">
            <div className="profile__avatar-container">
            <img className="profile__avatar" src={avatar} alt="Аватар" onClick={onEditAvatar} style={{ backgroundImage: `url(${userAvatar})` }} />
            <img className="profile__avatar-edit" src={avatarEdit} alt="Ручка"/>
            </div>
            <div className="info">
                <h1 className="info__name">Жак-Ив Кусто</h1>
                <button type="button" className="button button_type_edit" onClick={onEditProfile}><img className="info__button-img" src={buttonEdit} alt="Кнопка редактирования"/></button>
                <p className="info__about">Исследователь океана</p>
            </div>
            <button type="button" className="button button_type_add-card" onClick={onAddPlace}><img className="profile__button-img" src={buttonAdd} alt="Кнопка добавления новой карточки"/></button>
        </section>

        <section>
            <ul className="cards" id="cards">


            </ul>
       </section>
    </main>
    )
}

export default Main;
