import React from "react";
import Popup from "./Popup";

const EditAvatarPopup = ({isOpen, onClose, onUpdateAvatar, isLoading}) => {
    const fields = ['avatar'];

    const {
        isValid, setIsValid,
        inputValue, setInputValue,
        validationMessage, setValidationMessage,
        handleInputChange, fieldsEnumeration
    } = useValidation(fields);

    const avatarInputRef = React.useRef(null);

    const handleSubmit = (event) => {
        event.preventDefault();
        onUpdateAvatar({
            avatar: avatarInputRef.current.value,
        });
    }

    React.useEffect(() => {
        setInputValue(fieldsEnumeration(''));
        setIsValid(fieldsEnumeration(false));
        setValidationMessage(fieldsEnumeration(''));
    }, [isOpen, setInputValue, setIsValid, setValidationMessage]);


    return (
        <Popup name="avatar-edit"
               isOpen={isOpen}
               onClose={onClose}>
            <h3 className="popup__title">Обновить аватар</h3>
           <form className={'popup__form popup__form_type_avatar-edit'} name="avataredit" onSubmit={handleSubmit} noValidate>
                <input type="url" id="avatarlink" name="avatarlink" className="popup__input popup__input_type_avatar-edit" placeholder="Ссылка на аватарку" required/>
                <span className="popup__span-error" id="avatarlink-error"></span>
                <button type="submit" className="popup__button popup__button_type_save popup__button_type_avatar-edit popup__button_disabled">Сохранить</button>
            </form>
        </Popup>
    )
}

export default EditAvatarPopup;
