import React from 'react';

const PopupWithForm = ({name, isOpen, onClose, children}) => {
    const handleEscapeClose = (event) => {
        if (event.key === 'Escape') {
            onClose();
        }
    };

    const handleOverlayClose = (event) => {
        if (event.target === event.currentTarget && isOpen) {
            onClose();
        }
    };

    React.useEffect(() => {
        document.addEventListener("keydown", handleEscapeClose, false);

        return () => {
            document.removeEventListener("keydown", handleEscapeClose, false);
        };
    }, [isOpen]);





    return (
        <article className={`${isOpen ? `popup popup_type_${name} popup_type_opened` : `popup popup_type_${name}`}`}
                 onMouseUp={handleOverlayClose}>
            <div className={`${(name === "popup__zoomed") ? `popup__zoomed` : `popup__container`}`}>
                <button className="popup__button popup__button_type_close"
                        type="button"
                        onClick={onClose}/>
                {children}
            </div>
        </article>
    );
}

export default PopupWithForm;
