import React from 'react';
import PopupWithForm from './PopupWithForm';

const AddNewCardPopup = ({isOpen, onClose, onAddPlace, isLoading}) => {
    const fields = ['name', 'link'];

    const {
        isValid, setIsValid,
        inputValue, setInputValue,
        validationMessage, setValidationMessage,
        handleInputChange, fieldsEnumeration
    } = useValidation(fields);

    const handleSubmit = (event) => {
        event.preventDefault();
        onAddPlace({
            name: inputValue.name,
            link: inputValue.link
        });
    }

    React.useEffect(() => {
        setInputValue(fieldsEnumeration(''));
        setIsValid(fieldsEnumeration(false));
        setValidationMessage(fieldsEnumeration(''));
    }, [isOpen, setInputValue, setIsValid, setValidationMessage]);

    return (
        <PopupWithForm name="add-card" isOpen={isOpen} onClose={onClose}>
            <h3 className="popup__title">Новое место</h3>
            <form className={'popup__form popup__form_type_new-card'} name="add" noValidate>
              <input type="text" id="cardname" name="cardname" className="popup__input popup__input_type_card-name" placeholder="Название" minLength="2" maxLength="30" required/>
              <span className="popup__span-error" id="cardname-error"></span>
              <input type="url" id="cardlink" name="cardlink" className="popup__input popup__input_type_card-link" placeholder="Ссылка на картинку" required/>
              <span className="popup__span-error" id="cardlink-error"></span>
            <button type="submit" className="popup__button popup__button_type_save popup__button_type_save-card popup__button_disabled" disabled>Создать</button>
           </form>
        </PopupWithForm>
    )
}

export default AddNewCardPopup;
